import React from 'react';
import './Profile.css';

import 'tachyons';

const Profile = ({ appState }) => {
    const profileImage = `https://robohash.org/${appState.userProfile.name}.png`

    return(
        <div className="profile-container flex flex-column pa2 tc">

            <div className="f2">
            <img src={profileImage} alt={appState.userProfile.name} /> </div>
            <div className="f2"> {appState.userProfile.name} </div>
            <div className="f7"> {appState.userProfile.email} </div>

            <div className="stats-container">
            <div className="f3">Gam Stats:</div>
            <p> Questions Attempted: 75</p>
            <p> Correctly Answered: 70</p>
            <p> Success Percentage: {70/75 * 100}</p>
            </div>

        </div>
    )
}


export default Profile;
